import React from "react";
import { Game } from "../hooks/useGames";
import { Link, useParams, useNavigate } from "react-router-dom";

interface Props {
  games: Game[];
}

const GameDetail: React.FC<Props> = ({ games }) => {
  const { gameId } = useParams<{ gameId: string }>();
  const gameIndex = games.findIndex((game) => game.id.toString() === gameId);
  const game = games[gameIndex];
  const navigate = useNavigate(); // Use the useNavigate hook

  if (!game) {
    return <div>Game not found</div>;
  }

  return (
    <div style={{ display: "flex", padding: "20px" }}>
      <img
        src={game.background_image}
        alt={game.name}
        style={{ width: "50%", height: "auto", marginRight: "20px" }}
      />

      <div style={{ flex: 1 }}>
        <h1>{game.name}</h1>
        <p>{/* other game details */}</p>

        <Link to={gameIndex > 0 ? `/game/${games[gameIndex - 1].id}` : "#"}>
          Previous
        </Link>

        <Link
          to={
            gameIndex < games.length - 1
              ? `/game/${games[gameIndex + 1].id}`
              : "#"
          }
        >
          Next
        </Link>

        <button onClick={() => navigate("/")}>Go to Gallery</button>
      </div>
    </div>
  );
};

export default GameDetail;
