import {
  Button,
  Menu,
  MenuButton,
  MenuItem,
  MenuList,
  Stack,
} from "@chakra-ui/react";
import React from "react";
import { BsChevronDown } from "react-icons/bs";

interface Props {
  onSelectSortOrder: (sortOrder: string) => void;
  onSelectSortDirection: (sortDirection: string) => void;
  sortOrder: string;
  sortDirection: string;
}

const SortSelector = ({
  onSelectSortOrder,
  sortOrder,
  onSelectSortDirection,
  sortDirection,
}: Props) => {
  const sortOrders = [
    { value: "name", label: "Name" },
    { value: "released", label: "Release date" },
    { value: "metacritic", label: "Popularity" },
    { value: "rating", label: "Rating" },
  ];

  const sortDirections = [
    { value: "asc", label: "Ascending" },
    { value: "desc", label: "Descending" },
  ];

  const currentSortOrder = sortOrders.find(
    (order) => order.value === sortOrder
  );
  const currentSortDirection = sortDirections.find(
    (direction) => direction.value === sortDirection
  );

  return (
    <Stack direction="row" spacing={4}>
      {/* Existing dropdown for sort orders */}
      <Menu>
        <MenuButton as={Button} rightIcon={<BsChevronDown />}>
          Order by: {currentSortOrder?.label || "Relevance"}
        </MenuButton>
        <MenuList>
          {sortOrders.map((order) => (
            <MenuItem
              onClick={() => {
                console.log("Sort Direction Selected:", order.value);
                onSelectSortDirection(order.value);
              }}
              key={order.value}
              value={order.value}
            >
              {order.label}
            </MenuItem>
          ))}
        </MenuList>
      </Menu>

      {/* New dropdown for sort direction */}
      <Menu>
        <MenuButton as={Button} rightIcon={<BsChevronDown />}>
          Direction: {currentSortDirection?.label || "Ascending"}
        </MenuButton>
        <MenuList>
          {sortDirections.map((direction) => (
            <MenuItem
              onClick={() => {
                console.log("Sort Order Selected:", direction.value);
                onSelectSortOrder(direction.value);
              }}
              key={direction.value}
              value={direction.value}
            >
              {direction.label}
            </MenuItem>
          ))}
        </MenuList>
      </Menu>
    </Stack>
  );
};

export default SortSelector;
