import React from "react";
import {
  Button,
  Menu,
  MenuButton,
  MenuItem,
  MenuList,
  Stack,
} from "@chakra-ui/react";
import { BsChevronDown } from "react-icons/bs";

interface Props {
  onSortChange: (sortOrder: string, sortDirection: string) => void;
  sortOrder: string;
  sortDirection: string;
}

const SortSelector: React.FC<Props> = ({
  onSortChange,
  sortOrder,
  sortDirection,
}) => {
  const sortOrders = [
    { value: "", label: "Relevance" },
    { value: "name", label: "Name" },
    { value: "released", label: "Release date" },
    { value: "metacritic", label: "Popularity" },
  ];

  const directions = [
    { value: "asc", label: "Ascending" },
    { value: "desc", label: "Descending" },
  ];

  const currentSortOrder = sortOrders.find(
    (order) => order.value === sortOrder
  );
  const currentDirection = directions.find(
    (dir) => dir.value === sortDirection
  );

  return (
    <Stack direction="row" spacing={4}>
      <Menu>
        <MenuButton as={Button} rightIcon={<BsChevronDown />}>
          Order by: {currentSortOrder?.label || "Relevance"}
        </MenuButton>
        <MenuList>
          {sortOrders.map((order) => (
            <MenuItem
              key={order.value}
              onClick={() => onSortChange(order.value, sortDirection)}
            >
              {order.label}
            </MenuItem>
          ))}
        </MenuList>
      </Menu>
      <Menu>
        <MenuButton as={Button} rightIcon={<BsChevronDown />}>
          Direction: {currentDirection?.label}
        </MenuButton>
        <MenuList>
          {directions.map((dir) => (
            <MenuItem
              key={dir.value}
              onClick={() => onSortChange(sortOrder, dir.value)}
            >
              {dir.label}
            </MenuItem>
          ))}
        </MenuList>
      </Menu>
    </Stack>
  );
};

export default SortSelector;
