import {
  Button,
  Menu,
  MenuButton,
  MenuItem,
  MenuList,
  Radio,
  RadioGroup,
  Stack,
} from "@chakra-ui/react";
import React, { useState } from "react";
import { BsChevronDown } from "react-icons/bs";

interface Props {
  onSelectSortOrder: (sortOrder: string) => void;
  sortOrder: string;
}

const SortSelector = ({ onSelectSortOrder, sortOrder }: Props) => {
  const [sortDirection, setSortDirection] = useState<string>("Descending");

  let sortOrders = [
    { value: "name", label: "Name" },
    { value: "released", label: "Release date" },
    { value: "metacritic", label: "Popularity" },
  ];

  // Modify sort orders based on the sort direction
  if (sortDirection === "Descending") {
    sortOrders = sortOrders.map((order) => {
      if (order.value.startsWith("-")) return order;
      return { ...order, value: `-${order.value}` };
    });
  }

  const currentSortOrder = sortOrders.find(
    (order) => order.value === sortOrder
  );

  return (
    <Menu>
      <MenuButton as={Button} rightIcon={<BsChevronDown />}>
        Order by: {currentSortOrder?.label || "Popularity"}
      </MenuButton>
      <MenuList>
        <Stack spacing={3} px={4}>
          <RadioGroup
            value={sortDirection}
            onChange={(val) => {
              setSortDirection(val as string);
              // Trigger a sort order change if direction changes
              onSelectSortOrder(
                sortOrder.startsWith("-")
                  ? sortOrder.substring(1)
                  : `-${sortOrder}`
              );
            }}
          >
            <Stack direction="row">
              <Radio value="Ascending">Ascending</Radio>
              <Radio value="Descending">Descending</Radio>
            </Stack>
          </RadioGroup>
          {sortOrders.map((order) => (
            <MenuItem
              onClick={() => onSelectSortOrder(order.value)}
              key={order.value}
              value={order.value}
            >
              {order.label}
            </MenuItem>
          ))}
        </Stack>
      </MenuList>
    </Menu>
  );
};

export default SortSelector;
