import React from "react";
import { Button, Menu, MenuButton, MenuItem, MenuList } from "@chakra-ui/react";
import { BsChevronDown } from "react-icons/bs";

interface Props {
  onSortChange: (sortOrder: string, sortDirection: string) => void;
  sortOrder: string;
  sortDirection: string;
}

const SortSelector: React.FC<Props> = ({
  onSortChange,
  sortOrder,
  sortDirection,
}) => {
  const sortOrders = [
    { value: "", label: "Relevance" },
    { value: "name", label: "Name" },
    { value: "released", label: "Release date" },
    { value: "metacritic", label: "Popularity" },
  ];

  const currentSortOrder = sortOrders.find(
    (order) => order.value === sortOrder
  );

  return (
    <Menu>
      <MenuButton as={Button} rightIcon={<BsChevronDown />}>
        Order by: {currentSortOrder?.label || "Relevance"}
      </MenuButton>
      <MenuList>
        {sortOrders.map((order) => (
          <MenuItem
            key={order.value}
            onClick={() => {
              const newOrder = order.value.startsWith("-")
                ? order.value.substring(1)
                : order.value;
              const newDirection = order.value.startsWith("-") ? "desc" : "asc";

              if (sortOrder === newOrder && sortDirection === "asc") {
                onSortChange(newOrder, "desc");
              } else {
                onSortChange(newOrder, newDirection);
              }
            }}
          >
            {order.label}
          </MenuItem>
        ))}
        <MenuItem
          onClick={() =>
            sortDirection === "asc"
              ? onSortChange(sortOrder, "desc")
              : onSortChange(sortOrder, "asc")
          }
        >
          {sortDirection === "asc" ? "Descending" : "Ascending"}
        </MenuItem>
      </MenuList>
    </Menu>
  );
};

export default SortSelector;
