import {
  Box,
  Button,
  Menu,
  MenuButton,
  MenuItem,
  MenuList,
} from "@chakra-ui/react";
import React from "react";
import { BsChevronDown } from "react-icons/bs";

interface Props {
  onSortChange: (sortOrder: string, sortDirection: string) => void;
  sortOrder: string;
  sortDirection: string;
}

const SortSelector = ({ onSortChange, sortOrder, sortDirection }: Props) => {
  const sortOrders = [
    { value: "", label: "Relevance" },
    { value: "name", label: "Name" },
    { value: "released", label: "Release date" },
    { value: "metacritic", label: "Popularity" },
  ];

  const currentSortOrder = sortOrders.find(
    (order) => order.value === sortOrder
  );

  const handleSortOptionClick = (orderValue: string) => {
    const direction = orderValue.startsWith("-") ? "desc" : "asc";
    const order = orderValue.replace("-", "");
    onSortChange(order, direction);
  };

  return (
    <Menu>
      <Box paddingRight={5}>
        <MenuButton as={Button} rightIcon={<BsChevronDown />}>
          Order by: {currentSortOrder?.label || "Relevance"}
        </MenuButton>
        <MenuList>
          {sortOrders.map((order) => (
            <MenuItem
              onClick={() => onSelectSortOrder(order.value)}
              key={order.value}
              value={order.value}
            >
              {order.label}
            </MenuItem>
          ))}
        </MenuList>
      </Box>
      <Menu>
        <MenuButton as={Button}>
          Sort Direction: {sortDirection === "asc" ? "Ascending" : "Descending"}
        </MenuButton>
        <MenuList>
          <MenuItem onClick={() => onSelectSortDirection("asc")}>
            Ascending
          </MenuItem>
          <MenuItem onClick={() => onSelectSortDirection("desc")}>
            Descending
          </MenuItem>
        </MenuList>
      </Menu>
    </Menu>
  );
};

export default SortSelector;
