import React from "react";
import { Game } from "../hooks/useGames";
import { Link, useParams, useNavigate } from "react-router-dom";

interface Props {
  games: Game[];
}

const GameDetail: React.FC<Props> = ({ games }) => {
  const { gameId } = useParams<{ gameId: string }>();
  const gameIndex = games.findIndex((game) => game.id.toString() === gameId);
  const game = games[gameIndex];
  const navigate = useNavigate();

  if (!game) {
    return <div>Game not found</div>;
  }

  return (
    <div className="game-detail-container">
      <h1 style={{ marginBottom: "20px" }}>{game.name}</h1>

      <div
        style={{
          display: "flex",
          width: "80%",
          justifyContent: "space-between",
        }}
      >
        <Link to={gameIndex > 0 ? `/game/${games[gameIndex - 1].id}` : "#"}>
          {" "}
          {"<"}{" "}
        </Link>

        <div
          style={{
            display: "flex",
            width: "90%",
            border: "1px solid lightgray",
            padding: "20px",
            borderRadius: "10px",
          }}
        >
          <img
            src={game.background_image}
            alt={game.name}
            style={{
              width: "40%",
              height: "auto",
              marginRight: "20px",
              borderRadius: "10px",
            }}
          />

          <div>
            <p>{/* game description */}</p>
            <p>Rating: {game.metacritic}</p>
            <p>Released: {game.released}</p>
            <p>Runtime: {game.playtime} hours</p>
          </div>
        </div>

        <Link
          to={
            gameIndex < games.length - 1
              ? `/game/${games[gameIndex + 1].id}`
              : "#"
          }
        >
          {" "}
          {">"}{" "}
        </Link>
      </div>

      <button
        onClick={() => navigate("/")}
        style={{ marginTop: "20px", padding: "10px 20px", borderRadius: "5px" }}
      >
        Go to Gallery
      </button>
    </div>
  );
};

export default GameDetail;
