import { Button, Menu, MenuButton, MenuItem, MenuList } from "@chakra-ui/react";
import React from "react";
import { BsChevronDown } from "react-icons/bs";

interface Props {
  onSelectSortOrder: (sortOrder: string) => void;
  onSelectSortDirection: (direction: "asc" | "desc") => void;
  sortOrder: string;
  sortDirection: "asc" | "desc";
}

const SortSelector = ({
  onSelectSortOrder,
  sortOrder,
  onSelectSortDirection,
  sortDirection,
}: Props) => {
  const baseSortOrders = [
    { value: "name", label: "Name" },
    { value: "released", label: "Release date" },
    { value: "metacritic", label: "Popularity" },
  ];

  const sortOrders = baseSortOrders.map((order) => {
    return {
      ...order,
      value: sortDirection === "desc" ? `-${order.value}` : order.value,
    };
  });

  const currentSortOrder = sortOrders.find(
    (order) => order.value === sortOrder
  );

  return (
    <Menu>
      {/* Radio buttons for Ascending & Descending */}
      <div>
        <label>
          <input
            type="radio"
            value="asc"
            checked={sortDirection === "asc"}
            onChange={() => {
              onSelectSortDirection("asc");
              const correctedSortOrder = sortOrder.replace("-", "");
              onSelectSortOrder(correctedSortOrder);
            }}
          />
          Ascending
        </label>
        <label>
          <input
            type="radio"
            value="desc"
            checked={sortDirection === "desc"}
            onChange={() => {
              onSelectSortDirection("desc");
              const correctedSortOrder = `-${sortOrder}`;
              onSelectSortOrder(correctedSortOrder);
            }}
          />
          Descending
        </label>
      </div>

      <MenuButton as={Button} rightIcon={<BsChevronDown />}>
        Order by: {currentSortOrder?.label || "Popularity"}
      </MenuButton>
      <MenuList>
        {sortOrders.map((order) => (
          <MenuItem
            onClick={() => onSelectSortOrder(order.value)}
            key={order.value}
            value={order.value}
          >
            {order.label}
          </MenuItem>
        ))}
      </MenuList>
    </Menu>
  );
};
