import React from "react";
import {
  Button,
  Menu,
  MenuButton,
  MenuItem,
  MenuList,
  Select,
} from "@chakra-ui/react";
import { BsChevronDown } from "react-icons/bs";

interface Props {
  onSelectSortOrder: (sortOrder: string, sortDirection: string) => void;
  sortOrder: string;
  sortDirection: string;
}

const SortSelector = ({
  onSelectSortOrder,
  sortOrder,
  sortDirection,
}: Props) => {
  const sortOrders = [
    { value: "", label: "Relevance" },
    { value: "name", label: "Ascending" },
    { value: "-released", label: "Release date" },
    { value: "-metacritic", label: "Popularity" },
  ];

  const sortDirections = [
    { value: "asc", label: "Ascending" },
    { value: "desc", label: "Descending" },
  ];

  const currentSortOrder = sortOrders.find(
    (order) => order.value === sortOrder
  );
  const currentSortDirection = sortDirections.find(
    (direction) => direction.value === sortDirection
  );

  const handleSelectSortOrder = (newSortOrder: string) => {
    const order = sortDirection === "desc" ? `-${newSortOrder}` : newSortOrder;
    onSelectSortOrder(order, sortDirection);
  };

  const handleSelectSortDirection = (newSortDirection: string) => {
    const order = newSortDirection === "desc" ? `-${sortOrder}` : sortOrder;
    onSelectSortOrder(order, newSortDirection);
  };

  return (
    <Menu>
      <MenuButton as={Button} rightIcon={<BsChevronDown />}>
        Order by: {currentSortOrder?.label || "Relevance"}
      </MenuButton>
      <MenuList>
        {sortOrders.map((order) => (
          <MenuItem
            onClick={() => handleSelectSortOrder(order.value)}
            key={order.value}
          >
            {order.label}
          </MenuItem>
        ))}
      </MenuList>
      <Select
        value={sortDirection}
        onChange={(e) => handleSelectSortDirection(e.target.value)}
      >
        {sortDirections.map((direction) => (
          <option key={direction.value} value={direction.value}>
            {direction.label}
          </option>
        ))}
      </Select>
    </Menu>
  );
};

export default SortSelector;
