import {
  Button,
  Menu,
  MenuButton,
  MenuItem,
  MenuList,
  Stack,
} from "@chakra-ui/react";
import React from "react";
import { BsChevronDown } from "react-icons/bs";

interface Props {
  onSelectSortOrder: (sortOrder: string) => void;
  onSelectOrderDirection: (orderDirection: string) => void;
  sortOrder: string;
  orderDirection: string;
}

const SortSelector = ({
  onSelectSortOrder,
  onSelectOrderDirection,
  sortOrder,
  orderDirection,
}: Props) => {
  const sortOrders = [
    { value: "", label: "Relevance" },
    { value: "name", label: "Name" },
    { value: "released", label: "Release date" },
    { value: "metacritic", label: "Popularity" },
  ];

  const orderDirections = [
    { value: "", label: "Ascending" },
    { value: "-", label: "Descending" },
  ];

  const currentSortOrder = sortOrders.find(
    (order) => order.value === sortOrder
  );

  return (
    <Stack direction="row" spacing={2}>
      <Menu>
        <MenuButton as={Button} rightIcon={<BsChevronDown />}>
          Order by: {currentSortOrder?.label || "Relevance"}
        </MenuButton>
        <MenuList>
          {sortOrders.map((order) => (
            <MenuItem
              onClick={() => onSelectSortOrder(order.value)}
              key={order.value}
              value={order.value}
            >
              {order.label}
            </MenuItem>
          ))}
        </MenuList>
      </Menu>
      <Menu>
        <MenuButton as={Button} rightIcon={<BsChevronDown />}>
          Direction: {orderDirection === "-" ? "Descending" : "Ascending"}
        </MenuButton>
        <MenuList>
          {orderDirections.map((direction) => (
            <MenuItem
              onClick={() => onSelectOrderDirection(direction.value)}
              key={direction.value}
              value={direction.value}
            >
              {direction.label}
            </MenuItem>
          ))}
        </MenuList>
      </Menu>
    </Stack>
  );
};

export default SortSelector;
