// GameDetail.tsx

import { Box, Heading, Image, Text } from "@chakra-ui/react";
import { useParams, Link } from "react-router-dom";
import croppedImageURL from "../services/cropImageinURL";
import { Game } from "../hooks/useGames";

interface Props {}

const GameDetail = () => {
  const { gameId } = useParams<{ gameId: string }>();
  // Fetch the game details using the gameId from the API or some state

  const game: Game = {
    id: 0,
    name: "",
    background_image: "",
    metacritic: "",
  }; // Fetch the game details using the gameId

  if (!game) {
    return <div>Loading...</div>; // Handle loading state
  }

  return (
    <Box>
      <Link to="/gallery">Back to Gallery</Link>
      <Image src={croppedImageURL(game.background_image)} />
      <Box>
        <Heading>{game.name}</Heading>
        <Text>Critic Score: {game.metacritic}</Text>
        {/* Display other attributes as needed */}
      </Box>
      {/* Add Previous and Next buttons if you have a list of games */}
      {/* You may need to handle the logic for previous and next */}
    </Box>
  );
};

export default GameDetail;
